mapper.select:
	SELECT * FROM ( select ROWNUM RN, A.*FROM(
	SELECT *
	FROM CONTEST
	WHERE SAVE = 'N'


) A WHERE ROWNUM <= #{last}) WHERE RN >=#{first}

mapper.update:
update contest
set INQ = INQ+1
where c_no = #{cNo}

mapper.select:
SELECT count(*)
from CONTEST a  join LIKES b
                on a.c_no = b.target_no
and a.C_NO = #{cNo}
and b.category = #{category}
