mapper.select:
SELECT USER_ID, USER_PASSWORD, USER_EMAIL, USER_NAME NAME, USER_PHONE, USER_MESSAGING, USER_GRADE AUTH, RPRT, DEPOTR, BANK, ACCNT, USER_REGDATE, USER_STATE FROM (SELECT ROWNUM RN, A.* FROM (SELECT * FROM MEMBERS
ORDER BY USER_REGDATE DESC
) A WHERE ROWNUM <= #{last}) WHERE RN >= #{first}

SELECT CRTF_ID, USER_ID, CRTF_NAME, CRTF_NUM, ACCP_YN, CRTF_TYPE, ACCP_DATE, REQ_DATE FROM (SELECT ROWNUM RN, A.* FROM (SELECT * FROM CERTIFICATE WHERE ACCP_YN = #{accpYN}
ORDER BY REQ_DATE DESC
) A WHERE ROWNUM <= #{last}) WHERE RN >= #{first}

mapper.update:
UPDATE CERTIFICATE
SET ACCP_YN = #{accpYN}, ACCP_DATE = SYSDATE
WHERE CRTF_ID = #{crtfId}

mapper.select:
SELECT COUNT(*)
FROM PUNISH
WHERE BAN_TYPE = #{banType}

mapper.update:
UPDATE MEMBERS
SET RPRT = RPRT + 1
WHERE USER_ID = #{targetId}

UPDATE CONTEST
SET RPRT = RPRT + 1
WHERE C_NO = #{targetId}

UPDATE JOB_BOARD
SET RPRT = RPRT + 1
WHERE JOB_NO = #{targetId}

UPDATE B_BOARD
SET RPRT = RPRT + 1
WHERE B_BOARD_NO = #{targetId}

UPDATE MONEY 
SET PAY_NO = #{payNo},
	SETT_YN = #{settYN},
	SETT_DT = #{settDt}
WHERE MO_NUM = #{moNum}

UPDATE CONTEST
SET SETT_YN = 'Y'
WHERE C_NO = #{coNo}
