mapper.select:
SELECT b.*, l.like_no
FROM B_BOARD b JOIN LIKES l
ON b.b_board_no = l.target_no
WHERE l.user_id = #{writer}
AND b.type = #{type}
ORDER BY ${order} DESC

mapper.update:
UPDATE B_BOARD
SET INQ = INQ+1
WHERE B_BOARD_NO = #{bBoardNo}

UPDATE B_BOARD
SET TTL=#{ttl}, CNTN=#{cntn}, IMG=#{img}
WHERE B_BOARD_NO = #{bBoardNo}
