mapper.select:
SELECT RN,c_no,writer,ttl,cntn,dt_reg,dt_cls,dt_extns,pay,save,inq,rprt ,REGEXP_REPLACE(REVERSE( REGEXP_REPLACE(REVERSE(TO_CHAR(pay)), '(\d{3})','\1,') ), '^,','') AS payComma ,code_select(category) category ,REPLACE(codeArr_select(style), ',', ' #') style ,dDay ,likes ,(select sev_nm from files where target_Id = c_no) AS img
FROM ( select ROWNUM RN, A.* FROM( SELECT c_no,writer,ttl,cntn,category,dt_reg,dt_cls,dt_extns,pay,save,inq,style,rprt ,CASE WHEN dt_extns IS NOT NULL THEN trunc(dt_extns) - trunc(sysdate) ELSE trunc(dt_cls) - trunc(sysdate) END AS dDay ,(select count(*) from likes where target_no = c_no) AS likes FROM CONTEST WHERE SAVE = 'N'
AND rprt < 1
AND NVL2(dt_extns,dt_extns,dt_cls) < sysdate
AND NVL2(dt_extns,dt_extns,dt_cls) >= sysdate
order by ${order}
) A WHERE ROWNUM <= #{last}) WHERE RN >=#{first}

SELECT * FROM ( select ROWNUM RN, A.*FROM( SELECT c_no, writer, ttl, cntn, dt_reg, dt_cls, dt_extns, pay, save, inq, rprt, code_select(category) category, codeArr_select(style) style, (select count(*) from winner where co_no = c_no and d_no is not null) AS winnerCheck, (select count(*) from design d where d.c_no = c.c_no and save = 'N')designCount,
CASE WHEN save = 'Y' THEN '임시저장' WHEN dt_extns IS NOT NULL THEN CASE WHEN trunc(dt_extns) >= trunc(SYSDATE) THEN '진행' ELSE '마감' END WHEN dt_extns IS NULL THEN CASE WHEN trunc(dt_cls) >= trunc(SYSDATE) THEN '진행' ELSE '마감' END END AS ing
FROM CONTEST c WHERE writer = #{writer}
ORDER BY TO_NUMBER(SUBSTR(c_no,3))DESC
) A WHERE ROWNUM <= #{last}) WHERE RN >=#{first}

	select count(*) 
      from contest
	 where writer = #{writer}

SELECT c_no,writer,ttl,cntn,dt_reg,dt_cls,dt_extns,pay,save,inq,rprt ,category categoryCode ,code_select(category) category ,REPLACE(codeArr_select(style), ',', ' #') style ,(select count(*) from likes where target_no = c_no) AS likes ,(select sev_nm from files where target_Id = c_no) AS img
, CASE WHEN dt_extns IS NOT NULL THEN CASE WHEN trunc(dt_extns) >= trunc(SYSDATE) THEN '진행중' ELSE '마감' END WHEN dt_extns IS NULL THEN CASE WHEN trunc(dt_cls) >= trunc(SYSDATE) THEN '진행중' ELSE '마감' END END AS ing
FROM CONTEST WHERE C_NO = #{cNo}

mapper.update:
update contest
set INQ = INQ+1
where c_no = #{cNo}

update CONTEST
set TTL = #{ttl},
	CNTN = #{cntn}
where c_no = #{cNo}

update CONTEST
	set DT_EXTNS = #{dtExtns}
where c_no = #{cNo}
