mapper.select:
SELECT * FROM (SELECT ROWNUM RN, A.* FROM ( SELECT d.dgn_no,d.user_id,d.c_no,d.name,d.cntn,d.win_yn,d.dt,d.pubc_yn,d.save,code_select(d.caregory)caregory, (select sev_nm from files where target_id = dgn_no and f_nm = center_img) AS center_img
FROM design d, likes l WHERE d.dgn_no = l.target_no(+) AND d.SAVE = 'N' AND d.PUBC_YN = 'N'
order by #{order} DESC
) A WHERE ROWNUM <= #{last}) WHERE RN >= #{first}

SELECT * FROM (SELECT ROWNUM RN, A.* FROM ( select c.c_no, d.caregory, d.dgn_no, d.user_id user_id, d.name, d.cntn, d.dt, d.center_img, d.pubc_yn, f.sev_nm sevNm
from contest c join design d on c.c_no = d.c_no join files f on f.target_id = d.dgn_no
and c.c_no = #{cNo} and d.center_img = f.f_nm and d.save = 'N' order by dt DESC
) A WHERE ROWNUM <= #{last}) WHERE RN >= #{first}

SELECT * FROM (SELECT ROWNUM RN, A.* FROM (
	SELECT d.*,f.sev_nm sevNm,c.ttl contestTtl 

	FROM DESIGN d join files f on f.target_id = d.dgn_no
	              join contest c  on d.c_no = c.c_no

	and USER_ID = #{userId}
	and d.center_img = f.f_nm
	and d.save = 'N'


	order by dt DESC

		) A WHERE ROWNUM <= #{last}) WHERE RN >= #{first}

mapper.update:
update design
set name = #{name},
    cntn = #{cntn},
    center_img = #{centerImg},
    pubc_yn = #{pubcYn},
    save = #{save}
where dgn_no = #{dgnNo}
and user_id = #{userId}

UPDATE DESIGN
   SET WIN_YN = 'Y'
 WHERE DGN_NO = #{dgnNo}

