mapper.select:
SELECT * FROM (SELECT ROWNUM RN, A.* FROM (
	SELECT *
	FROM design

	) A WHERE ROWNUM <= #{last}) WHERE RN >= #{first}

mapper.update:
update design
set name = #{name},
    cntn = #{cntn},
    center_img = #{centerImg},
    pubc_yn = #{pubcYn}
where dgn_no = #{dNo}
and user_id = #{userId}

mapper.delete:
delete design
where dgn_no = #{dNo}
and user_id = #{userId}
