mapper.select:
SELECT * FROM (SELECT ROWNUM RN, A.* FROM (
	SELECT *
	FROM design
	order by dt DESC

	) A WHERE ROWNUM <= #{last}) WHERE RN >= #{first}

SELECT * FROM DESIGN
WHERE USER_ID = #{userId}
order by dt DESC

SELECT * FROM DESIGN
WHERE dgn_no = #{dgnNo}
order by dt DESC

mapper.update:
update design
set name = #{name},
    cntn = #{cntn},
    center_img = #{centerImg},
    pubc_yn = #{pubcYn}
where dgn_no = #{dgnNo}
and user_id = #{userId}

mapper.delete:
delete design
where dgn_no = #{dgnNo}
and user_id = #{userId}
