mapper.select:
SELECT *
FROM LIKES
WHERE USER_ID = #{userId}
AND TARGET_NO = #{targetNo}

SELECT COUNT(*) 
FROM LIKES 
WHERE TARGET_NO=#{targetNo}

mapper.delete:
DELETE FROM LIKES
WHERE TARGET_NO = #{targetNo}
AND CATEGORY = #{category}

mapper.select:
select count(*)
from likes
where user_id = #{userId}
and target_No = #{targetNo}
and category = #{category}

select count(*)
from likes
where user_id = #{userId}
and category = #{category}
