mapper.select:
SELECT COUNT(*) 
FROM MEMBERS 
WHERE USER_ID = #{inputId}

SELECT USER_ID
FROM MEMBERS
WHERE USER_NAME = #{name}
AND USER_EMAIL = #{email}

SELECT COUNT(*) 
FROM MEMBERS 
WHERE USER_ID = #{id}
AND USER_NAME = #{name}
AND USER_EMAIL = #{email}

mapper.update:
UPDATE MEMBERS
SET USER_PASSWORD = #{userPassword}
WHERE USER_ID = #{userId}

UPDATE MEMBERS
SET user_state = #{userState}
WHERE USER_ID = #{userId}

		UPDATE busi_member
		SET busi_title = #{busiTitle},
            busi_type = #{busiType},
            busi_area = #{busiArea},
            busi_style=#{busiStyle}
		WHERE USER_ID = #{userId}

		UPDATE MEMBERS
		SET DEPOTR =#{depotr},
            bank = #{bank},
            ACCNT = #{accnt}
		WHERE USER_ID = #{userId}
