mapper.select:
	SELECT PRO_NO, USER_ID, E_NAME, code_select(E_TYPE) E_TYPE, IMG, SEE
	 FROM (SELECT ROWNUM RN, A.* 
	        FROM(SELECT * FROM PROMOTION_BOARD


		   ) A WHERE ROWNUM <= #{last}) 
    WHERE RN >= #{first}

