mapper.select:
SELECT * FROM (SELECT ROWNUM RN, A.* FROM ( SELECT DISTINCT D.DGN_NO, D.USER_ID, D.C_NO, D.NAME, CODEARR_SELECT(D.CAREGORY) CAREGORY, D.CNTN, D.WIN_YN, D.DT, D.PUBC_YN, D.SAVE, F.SEV_NM CENTER_IMG, (SELECT COUNT(*) FROM LIKES WHERE TARGET_NO = D.DGN_NO) AS CNT
FROM DESIGN D, FILES F, LIKES L WHERE D.CENTER_IMG = F.F_NM AND D.DGN_NO = F.TARGET_ID AND D.USER_ID = L.TARGET_NO AND D.USER_ID = #{userId}
) A WHERE ROWNUM <= #{last}) WHERE RN >=#{first}

SELECT COUNT(*)
FROM DESIGN
WHERE USER_ID = #{userId}

mapper.update:
UPDATE DGNER_PRTFL
   SET INQ = INQ + 1
 WHERE USER_ID = #{userId}
