mapper.select:
UPDATE MC_PRTFL
SET INQ = INQ+1
WHERE USER_ID = #{userId}

mapper.update:
UPDATE MC_PRTFL
SET ONE_INTRO = #{oneIntro}, CARR_YEAR = #{carrYear}, CASTING_PRICE = #{castingPrice}, MC_AREA = #{mcArea}, MC_TYPE = #{mcType}, PROG_STYLE = #{progStyle}, CAREER = #{career}
WHERE USER_ID = #{userId}
