mapper.select:
SELECT * FROM (SELECT ROWNUM RN, A.* FROM ( SELECT m.user_id, m.one_intro, m.carr_year, m.casting_price, codeArr_select(m.mc_area) mc_area, codeArr_select(m.mc_type) mc_type, codeArr_select(m.prog_style) prog_style, m.self_intro, m.career, m.inq, m.dt, m.user_name, (select count(*) from likes where target_no = m.user_id) AS cnt, f.sev_nm img FROM MC_PRTFL m, LIKES l, FILES F WHERE M.USER_ID = L.TARGET_NO(+) AND M.IMG = F.F_NM(+)
ORDER BY ${order} DESC
) A WHERE ROWNUM <= #{last}) WHERE RN >= #{first}

mapper.update:
UPDATE MC_PRTFL
SET INQ = INQ+1
WHERE USER_ID = #{userId}

UPDATE MC_PRTFL
SET ONE_INTRO = #{oneIntro}, CARR_YEAR = #{carrYear}, CASTING_PRICE = #{castingPrice}, MC_AREA = #{mcArea}, MC_TYPE = #{mcType}, PROG_STYLE = #{progStyle}, CAREER = #{career}, img = #{img}
WHERE USER_ID = #{userId}
