mapper.select:
SELECT * FROM (SELECT ROWNUM RN, A.* FROM (
SELECT m.*, (select count(*) from likes where target_no = m.user_id) AS cnt
FROM MC_PRTFL m, likes l
WHERE m.user_id = l.target_no(+)

ORDER BY ${order} DESC

	) A WHERE ROWNUM <= #{last}) WHERE RN >= #{first}

mapper.update:
UPDATE MC_PRTFL
SET INQ = INQ+1
WHERE USER_ID = #{userId}

UPDATE MC_PRTFL
SET ONE_INTRO = #{oneIntro}, CARR_YEAR = #{carrYear}, CASTING_PRICE = #{castingPrice}, MC_AREA = #{mcArea}, MC_TYPE = #{mcType}, PROG_STYLE = #{progStyle}, CAREER = #{career}
WHERE USER_ID = #{userId}
