mapper.select:
	SELECT B.*,
	CASE WHEN Q_NO IN (SELECT Q.Q_NO
						FROM QUESTIONS Q, QUESTIONS A
						WHERE Q.Q_NO = A.REREP_TGT) THEN '답변완료'
													ELSE '미답변'
						END AS ANS
	FROM ( select ROWNUM RN, A.*FROM(
			SELECT Q.*, NVL(rerep_tgt, Q_NO) GP
			FROM Questions Q
			WHERE category = #{category}
			and rerep_tgt is null
			and USER_ID = #{userId}

			ORDER BY GP DESC , REREP_TGT DESC, WRITING_DT DESC

		        ) A 
		    WHERE ROWNUM <= #{last}
		 ) B
    WHERE RN >= #{first}

	SELECT B.*,
	CASE WHEN Q_NO IN (SELECT Q.Q_NO
						FROM QUESTIONS Q, QUESTIONS A
						WHERE Q.Q_NO = A.REREP_TGT)  THEN '답변완료'
													 ELSE '미답변'
						END AS ANS
	FROM (SELECT ROWNUM RN, A.*
			FROM ( SELECT *
					FROM QUESTIONS QU
					WHERE USER_ID = #{vo.userId}

		        ) A 
		    WHERE ROWNUM <= #{paging.last}
		 ) B
    WHERE RN >= #{paging.first}

SELECT *
FROM QUESTIONS
WHERE CATEGORY = #{category}
AND TARGET_ID = #{targetId}
AND Q_NO = #{qNo}

SELECT *
FROM QUESTIONS
WHERE Q_NO = #{qNo}
OR REREP_TGT = #{rerepTgt}
ORDER BY REREP_TGT DESC

mapper.update:
UPDATE QUESTIONS
SET CNTN= #{Cntn},
WRITING_DT = current_date,
SECRET_YN = #{secretYn}
WHERE Q_NO = #{qNo}
AND USER_ID = #{userId}

mapper.delete:
DELETE QUESTIONS
WHERE Q_NO = #{qNo}
AND USER_ID =#{userId}

mapper.select:
SELECT COUNT(*)
FROM
QUESTIONS Q
WHERE CATEGORY = #{category}
AND USER_ID = #{userId}
AND	REREP_TGT IS NULL
