mapper.select:
select *
from Questions 
where target_id =#{targetId}
and category = #{category}

select * 
from Questions
where category = #{category}
and target_id = #{targetId}
and q_no = #{qNo}

mapper.update:
update QUESTIONS
set CNTN= #{Cntn},
	WRITING_DT = current_date,
	SECRET_YN = #{secretYn}
where q_no = #{qNo}
and user_id = #{userId}

mapper.delete:
delete questions
where q_no = #{qNo}
and user_id = #{userId}
