mapper.select:
	  SELECT R.*, NVL(REREP_TGT, REPLY_NO) GP FROM REPLY R
      WHERE REPLY_TGT = #{replyTgt} AND BOARD_CAT = #{boardCat}
      ORDER BY GP DESC , WRITING_DT DESC

mapper.update:
UPDATE REPLY
SET REPLY_CNTN = #{replyCntn}
WHERE REPLY_NO = #{replyNo}

mapper.delete:
DELETE FROM REPLY
WHERE REPLY_TGT = #{replyTgt}
AND BOARD_CAT = #{boardCat}
