mapper.select:
SELECT * FROM (SELECT ROWNUM RN, A.* FROM ( SELECT R.RESUME_NO, R.SEEKER_ID, R.TITLE, R.JOB_NO, (SELECT F.SEV_NM FROM FILES F WHERE F.TARGET_ID = R.RESUME_NO AND ROWNUM=1) AS "IMG", J.USER_ID, R.HIRE
FROM RESUME_BOARD R, JOB_BOARD J WHERE R.JOB_NO = J.JOB_NO AND J.JOB_NO IN (SELECT JOB_NO FROM JOB_BOARD WHERE USER_ID = #{seekerId})
) A WHERE ROWNUM <= #{last}) WHERE RN >= #{first}

SELECT * FROM (SELECT ROWNUM RN, A.* FROM ( SELECT R.RESUME_NO, R.SEEKER_ID, R.TITLE, R.JOB_NO, (SELECT SEV_NM FROM FILES WHERE TARGET_ID = RESUME_NO AND ROWNUM=1) AS "IMG", J.USER_ID
FROM RESUME_BOARD R, JOB_BOARD J WHERE R.JOB_NO = J.JOB_NO AND J.JOB_NO = #{jobNo}
) A WHERE ROWNUM <= #{last}) WHERE RN >= #{first}

SELECT COUNT(*)
FROM RESUME_BOARD
WHERE JOB_NO = #{jobNo}


SELECT USER_NAME, USER_EMAIL, USER_PHONE 
FROM MEMBERS
WHERE USER_ID = #{userId}

mapper.update:
UPDATE RESUME_BOARD
SET   HIRE = 'Y'
WHERE RESUME_NO = #{resumeNo}

UPDATE RESUME_BOARD
SET HIRE = 'N'
WHERE RESUME_NO = #{resumeNo}

